mapper.select:
select

FROM ${table}
where KZID=#{kzId}
AND STATUS = 1
ORDER BY ID DESC

mapper.insert:
INSERT INTO ${logTabName} (
    KZID, PAYSTYLE, AMOUNT, STAFFID, STAFFNAME, PAYMENTTIME, OPERAID, OPERANAME, CREATETIME, COMPANYID
)
VALUES
    (#{log.kzId}, #{log.payStyle}, #{log.amount}, #{log.staffId}, #{log.staffName}, #{log.paymentTime},
     #{log.operaId}, #{log.operaName}, UNIX_TIMESTAMP(NOW()), #{log.companyId}
    )

mapper.update:
UPDATE ${table}
SET AMOUNT = #{amount}
WHERE ID = #{id}
      AND COMPANYID = #{companyId}

mapper.select:
SELECT COUNT(log.AMOUNT)
FROM ${table} log
WHERE log.COMPANYID = #{companyId}
      AND log.KZID = #{kzId}
