mapper.insert:
        INSERT INTO ${logTabName} (
			KZID, PAYSTYLE, AMOUNT,  STAFFID,  STAFFNAME, PAYMENTTIME, OPERAID, OPERANAME,  CREATETIME, COMPANYID
		)
		VALUES
			(#{log.kzId}, #{log.payStyle}, #{log.amount}, #{log.staffId},#{log.staffName} ,#{log.paymentTime},#{log.operaId},#{log.operaName}, UNIX_TIMESTAMP(NOW()), #{log.companyId}
		)

mapper.update:
UPDATE ${table}
SET AMOUNT = #{amount}
WHERE ID = #{id}
AND COMPANYID = #{companyId}

mapper.select:
select log.ID,log.KZID,log.PAYSTYLE,log.AMOUNT,log.STAFFID,log.STAFFNAME,log.FROM_UNIXTIME(PAYMENTTIME, '%Y/%m/%d %H:%i') PAYMENTTIME,log.STATUS, dic.DICNAME AS PAYSTYLENAME
FROM  ${table} log
LEFT JOIN hm_crm_dictionary dic ON log.PAYSTYLE = dic.DICCODE AND log.COMPANYID = dic.COMPANYID
WHERE log.ID = #{id}
AND log.COMPANYID = #{companyId};
