mapper.select:
SELECT

FROM
hm_crm_channel
WHERE
ID = #{id}
AND COMPANYID = #{companyId}

mapper.update:
UPDATE
    hm_crm_channel
SET PRIORITY = #{priority}
WHERE
    COMPANYID = #{companyId} AND ID = #{id}

mapper.select:
SELECT COUNT(1)
FROM
    hm_crm_channel
WHERE
    CHANNELNAME = #{channelName}
    AND COMPANYID = #{companyId}

SELECT channel.ID, channel.CHANNELNAME, src.ID AS SRCID, src.SRCNAME FROM hm_crm_channel channel LEFT JOIN hm_crm_source src ON src.COMPANYID = channel.COMPANYID AND src.CHANNELID = channel.ID WHERE channel.COMPANYID = #{companyId} AND channel.ISSHOW = 1 AND src.ISSHOW = 1 AND channel.TYPEID IN
#{typeId}
ORDER BY channel.TYPEID, channel.PRIORITY, channel.ID, src.PRIORITY, src.ID

SELECT channel.ID, channel.CHANNELNAME, src.ID AS SRCID, src.SRCNAME FROM hm_crm_channel channel LEFT JOIN hm_crm_source src ON src.COMPANYID = channel.COMPANYID AND src.CHANNELID = channel.ID WHERE channel.COMPANYID = #{companyId} AND channel.TYPEID IN
#{typeId}
ORDER BY channel.TYPEID, channel.PRIORITY, channel.ID, src.PRIORITY, src.ID

SELECT
src.CHANNELID ID, src.CHANNELNAME, src.ID AS SRCID, src.SRCNAME
FROM hm_crm_client_remark info
LEFT JOIN hm_crm_source src ON info.SOURCEID = src.ID AND src.COMPANYID = #{companyId}
WHERE
info.ISDEL = 0
AND info.COMPANYID = #{companyId}
AND info.COLLECTORID = #{staffId}
AND DATE_SUB( CURDATE(), INTERVAL 10 DAY ) <= FROM_UNIXTIME( info.CREATETIME )
AND src.ISSHOW = 1
AND src.TYPEID IN

    #{typeId}

GROUP BY
info.CHANNELID, info.SOURCEID
ORDER BY COUNT( 1 ) DESC;
