mapper.update:
        UPDATE
	      ${tabName}


                WEFLAG = #{clientStatusVO.weFlag},


                SEX = #{clientStatusVO.sex}


        WHERE
	      KZID = #{clientStatusVO.kzId} AND COMPANYID = #{clientStatusVO.companyId}

UPDATE
${tabName}
SET
MEMO = CONCAT(#{label}, IFNULL(MEMO,''))
WHERE
COMPANYID = #{companyId}
AND KZID = #{kzId};
