mapper.update:
UPDATE
${tabName}


        WEFLAG = #{clientStatusVO.weFlag},


        SEX = #{clientStatusVO.sex}


WHERE
KZID = #{clientStatusVO.kzId} AND COMPANYID = #{clientStatusVO.companyId}

UPDATE
    ${tabName}
SET
    MEMO = CONCAT(#{label}, IFNULL(MEMO, ''))
WHERE
    COMPANYID = #{companyId}
    AND KZID = #{kzId};

mapper.select:
SELECT COUNT(1)
FROM ${tableName} info
WHERE info.ISDEL = 0
      AND info.STATUSID = #{statusId}
      AND info.COMPANYID = #{companyId}
