mapper.update:
UPDATE ${infoTabName}
SET CLASSID      = #{classId},
    STATUSID     = #{statusId},
    APPOINTORID  = #{appointorId},
    LASTPUSHTIME = UNIX_TIMESTAMP(NOW()),
    RECEIVETIME  = UNIX_TIMESTAMP(NOW()),
    UPDATETIME   = UNIX_TIMESTAMP(NOW()),
    GROUPID      = #{groupId},
    ALLOTTYPE    = #{allotType}
WHERE
    KZID = #{kzId}
    AND COMPANYID = #{companyId}

UPDATE ${infoTabName}
SET STATUSID     = #{statusId},
    SHOPID       = #{shopId},
    RECEPTORID   = #{receptorId},
    LASTPUSHTIME = UNIX_TIMESTAMP(NOW()),
    UPDATETIME   = UNIX_TIMESTAMP(NOW()),
    ALLOTTYPE    = #{allotType}
WHERE
    KZID = #{kzId}
    AND COMPANYID = #{companyId}

UPDATE ${detailTabName}
SET APPOINTNAME = #{appointorName}, GROUPNAME = #{groupName}
WHERE
    KZID = #{kzId}
    AND COMPANYID = #{companyId}

UPDATE ${detailTabName}
SET RECEPTORNAME = #{receptorName},
    SHOPNAME     = #{shopName}
WHERE
    KZID = #{kzId}
    AND COMPANYID = #{companyId}

UPDATE ${infoTabName}
SET RECEIVETIME = UNIX_TIMESTAMP(NOW()), UPDATETIME = UNIX_TIMESTAMP(NOW())
WHERE
    KZID = #{kzId}
    AND COMPANYID = #{companyId}

UPDATE ${infoTabName}
SET STATUSID   = #{statusId},
    CLASSID    = #{classId},
    UPDATETIME = UNIX_TIMESTAMP(NOW())
WHERE
    KZID = #{kzId}
    AND COMPANYID = #{companyId}

mapper.select:
SELECT log.ID, log.TYPEID, log.TYPENAME, log.BEFOREVALUE, log.AFTERVALUE, log.OPERANAME, FROM_UNIXTIME(log.OPERATIME, '%Y/%m/%d %H:%m:%s ') OPERATIME, info.KZNAME, info.STATUSID, info.SOURCEID, detail.APPOINTNAME, detail.COLLECTORNAME FROM ${tableEditLog} log LEFT JOIN ${tableInfo} info ON log.KZID = info.KZID LEFT JOIN ${tableDetail} detail ON log.KZID=detail.KZID LEFT JOIN hm_pub_staff staff on staff.ID=info.PROMOTORID AND staff.COMPANYID=info.COMPANYID
WHERE log.COMPANYID=#{companyId}
AND info.STATUSID=#{statusId}
AND info.SOURCEID IN (
#{sourceId}
)
AND log.TYPEID=#{typeId}
AND (log.BEFOREVALUE=#{value} OR log.AFTERVALUE=#{value})
AND log.OPERATIME >= #{startTime} AND log.OPERATIME < #{endTime}

mapper.update:
UPDATE ${infoTabName}
SET CLASSID     = #{classId},
    STATUSID    = #{statusId},
    APPOINTORID = #{appointorId},
    UPDATETIME  = UNIX_TIMESTAMP(NOW()),
    GROUPID     = #{groupId},
    ALLOTTYPE   = #{allotType}
WHERE
    KZID = #{kzId}
    AND COMPANYID = #{companyId}

mapper.select:
SELECT log.ID id, info.KZID kzId, info.KZNAME kzName, info.KZPHONE kzPhone, info.KZQQ kzqq, info.KZWECHAT kzWechat, detail.APPOINTNAME appointName, detail.COLLECTORNAME collectorName, log.MEMO memo, info.STATUSID statusId, info.SOURCEID sourceId, COUNT(log.KZID) count FROM ${tableLog} log LEFT JOIN ${tableInfo} info ON info.KZID = log.KZID LEFT JOIN ${tableDetail} detail ON log.KZID = detail.KZID WHERE log.COMPANYID = #{companyId} AND log.LOGTYPE = #{logType}
AND log.OPERATIME >= #{startTime} AND log.OPERATIME < #{endTime}
AND info.APPOINTORID=#{staffId}
GROUP BY log.KZID

mapper.update:
UPDATE ${detTabName}
SET STAYAMOUNT = (SELECT IFNULL(SUM(AMOUNT), 0)
                  FROM ${cashTabName}
                  WHERE KZID = #{kzId} AND COMPANYID = #{companyId} AND STATUS = 1)
WHERE
    KZID = #{kzId}
    AND COMPANYID = #{companyId};

UPDATE ${infoTabName}
SET PROMOTORID   = #{promotorId},
    LASTPUSHTIME = UNIX_TIMESTAMP(NOW()),
    UPDATETIME   = UNIX_TIMESTAMP(NOW())
WHERE
    KZID = #{kzId}
    AND COMPANYID = #{companyId}

UPDATE ${detTabName}
SET PROMOTERNAME = #{promoterName}
WHERE
    KZID = #{kzId}
    AND COMPANYID = #{companyId}

UPDATE ${infoTabName}
SET LASTPUSHTIME = UNIX_TIMESTAMP(NOW()),
    UPDATETIME   = UNIX_TIMESTAMP(NOW())
WHERE
    KZID = #{kzId}
    AND COMPANYID = #{companyId}
