mapper.select:
SELECT

FROM hm_pub_group
WHERE PARENTID = #{parentId}
AND COMPANYID = #{companyId}

SELECT

FROM hm_pub_group
WHERE COMPANYID = #{companyId}
AND GROUPNAME = #{groupName}

mapper.delete:
DELETE FROM hm_pub_group
  WHERE COMPANYID = #{companyId}
AND GROUPID = #{groupId}

update.foreach:
UPDATE hm_pub_group
SET GROUPTYPE = #{item.groupType}
WHERE ID = #{item.id}
AND COMPANYID = #{item.companyId}

mapper.select:
SELECT
    grp.GROUPID
FROM
    hm_pub_group grp
    LEFT JOIN hm_pub_group_staff rela ON rela.COMPANYID = grp.COMPANYID AND grp.GROUPID = rela.GROUPID
WHERE
    grp.COMPANYID = #{companyId}
    AND grp.GROUPTYPE = #{type}
    AND grp.PARENTID != '0'
    AND rela.STAFFID = #{staffId}

SELECT

FROM hm_pub_group
WHERE COMPANYID = #{companyId}
AND GROUPID = #{groupId}
