mapper.select:
SELECT

FROM hm_pub_group
WHERE PARENTID = #{parentId}
AND COMPANYID = #{companyId}

mapper.update:
UPDATE hm_pub_group


        GROUPID = #{groupId},


        GROUPNAME = #{groupName},


        PARENTID = #{parentId},


        GROUPTYPE = #{groupType},


        CHIEFIDS = #{chiefIds},


        CHIEFNAMES = #{chiefNames},


        SHOPID = #{shopId}


WHERE ID = #{id}
AND COMPANYID = #{companyId}

mapper.select:
SELECT

FROM hm_pub_group
WHERE COMPANYID = #{companyId}
AND GROUPNAME = #{groupName}

mapper.delete:
DELETE FROM hm_pub_group
WHERE COMPANYID = #{companyId}
      AND GROUPID = #{groupId}

update.foreach:
UPDATE hm_pub_group
SET GROUPTYPE = #{item.groupType}
WHERE ID = #{item.id}
AND COMPANYID = #{item.companyId}

mapper.select:
SELECT

FROM hm_pub_group
WHERE COMPANYID = #{companyId}
AND GROUPID = #{groupId}

SELECT grp.GROUPID,grp.GROUPNAME
FROM
hm_pub_group grp
LEFT JOIN hm_pub_group_staff rela ON rela.COMPANYID = grp.COMPANYID AND grp.GROUPID = rela.GROUPID
WHERE
grp.COMPANYID = #{companyId}
AND grp.PARENTID != '0'
AND rela.STAFFID = #{staffId}
AND grp.GROUPTYPE = #{type}
