mapper.select:
        SELECT
	        log.ALLOTTIME, log.STAFFID, log.STAFFNAME, info.ID kzNum, log.KZID, info.CHANNELID, info.SOURCEID
        FROM
	        ${logTab} log
        LEFT JOIN ${infoTab} info ON log.KZID = info.KZID AND info.COMPANYID = #{companyId}
        WHERE
	        log.COMPANYID = #{companyId}
        AND log.STAFFID = #{staffId}

        AND log.ALLOTTIME > UNIX_TIMESTAMP(DATE(SYSDATE()))
        ORDER BY log.ALLOTTIME DESC

SELECT log.ALLOTTIME, log.STAFFID, log.STAFFNAME, info.ID kzNum, log.KZID, info.CHANNELID, info.SOURCEID, s.SRCNAME FROM ${logTab} log LEFT JOIN ${infoTab} info ON log.KZID = info.KZID AND info.COMPANYID = #{lw.companyId} LEFT JOIN hm_crm_source s ON s.ID = info.SOURCEID AND log.COMPANYID = s.COMPANYID
log.COMPANYID = #{lw.companyId} AND log.STATUSID = 0
AND log.ALLOTTIME > #{lw.startTime} AND log.ALLOTTIME < #{lw.endTime}
AND log.STAFFID IN
#{id}
ORDER BY log.ALLOTTIME DESC
