mapper.select:
SELECT log.ALLOTTIME, log.STAFFID, log.STAFFNAME, info.ID kzNum, log.KZID, info.CHANNELID, info.SOURCEID,stf.PHONE staffPhone,stf.HEADIMG headImg, grp.GROUPID,grp.GROUPNAME FROM hm_crm_allot_log log LEFT JOIN hm_crm_client_info info ON log.KZID = info.KZID AND info.COMPANYID = #{lw.companyId} LEFT JOIN hm_pub_staff stf ON stf.ID = log.STAFFID AND stf.COMPANYID = log.COMPANYID LEFT JOIN hm_pub_group_staff gs ON gs.STAFFID = stf.ID AND gs.COMPANYID = log.COMPANYID LEFT JOIN hm_pub_group grp ON grp.GROUPID = gs.GROUPID AND grp.COMPANYID = log.COMPANYID
log.COMPANYID = #{lw.companyId} AND log.STATUSID = 0
AND log.ALLOTTIME > #{lw.startTime} AND log.ALLOTTIME < #{lw.endTime}
AND log.STAFFID IN
#{id}
AND info.SOURCEID IN
#{sourceId}
ORDER BY log.ALLOTTIME DESC
