mapper.insert:
        INSERT INTO ${logTabName} (
			STAFFID, STAFFNAME, ONLINETIME, COMPANYID, LINEDAY
		)
		VALUES
			( #{staffId}, #{staffName}, 0, #{companyId}, UNIX_TIMESTAMP(CAST(SYSDATE()AS DATE))
		)

mapper.update:
UPDATE ${logTabName}
SET ONLINETIME = ONLINETIME + #{time}
WHERE COMPANYID = #{companyId}
AND STAFFID = #{staffId}
AND LINEDAY = UNIX_TIMESTAMP(CAST(SYSDATE()AS DATE))

mapper.select:
SELECT

FROM ${logTabName} log
WHERE COMPANYID = #{companyId}
AND STAFFID = #{staffId}
AND LINEDAY = UNIX_TIMESTAMP(CAST(SYSDATE()AS DATE))
