mapper.update:
UPDATE hm_crm_page_conf conf
SET
    conf.MEMO         = #{memo},
    conf.SHOWFLAG     = #{showFlag},
    conf.ACTIONSQL    = #{actionSql},
    conf.SQLCONDITION = #{sqlCondition},
    conf.TITLETXT     = #{titleTxt},
    conf.TITLETXT_NO  = #{titleTxtNo}
WHERE conf.COMPANYID = #{companyId}
      AND conf.ID = #{id}

mapper.select:
SELECT ACTIONSQL
FROM
    hm_crm_page_conf
WHERE
    COMPANYID = #{companyId}
    AND ROLE = #{role}
    AND ACTION = #{action}
