mapper.select:
SELECT

FROM
hm_crm_source
WHERE
ID = #{id}
AND COMPANYID = #{companyId}

mapper.update:
UPDATE
hm_crm_source


        PRIORITY = #{priority}


WHERE
COMPANYID = #{companyId} AND ID = #{id}

UPDATE
  hm_crm_source
SET
  CHANNELNAME = #{channelName}
WHERE
  COMPANYID = #{companyId} AND CHANNELID = #{channelId}

mapper.select:
SELECT src.ID FROM ${tableName} info LEFT JOIN hm_crm_source src ON src.COMPANYID = info.COMPANYID AND src.ID = info.SOURCEID WHERE info.ISDEL = 0 AND info.COMPANYID = #{companyId} AND info.COLLECTORID = #{staffId} AND DATE_SUB( CURDATE( ), INTERVAL 30 DAY ) <= FROM_UNIXTIME( info.CREATETIME ) AND src.ISSHOW = 1 AND src.TYPEID IN
#{typeId}
GROUP BY src.ID ORDER BY COUNT( 1 ) DESC;

SELECT

FROM
hm_crm_source
WHERE
SRCNAME = #{srcName}
AND TYPEID = #{typeId}
AND COMPANYID = #{companyId}
